package org.zfin.datatransfer.ctd;import org.hibernate.Session;import javax.persistence.Table;import java.util.List;public class MeshChebiDAO {    protected Session entityManager;    public MeshChebiDAO(Session entityManager) {        this.entityManager = entityManager;    }    public MeshChebiMapping find(long id) {        return entityManager.load(MeshChebiMapping.class, id);    }    List<MeshChebiMapping> allMeshChebiMapping;    public List<MeshChebiMapping> findAll() {        if (allMeshChebiMapping != null) return allMeshChebiMapping;        allMeshChebiMapping = entityManager.createQuery("from MeshChebiMapping ", MeshChebiMapping.class).getResultList();        return allMeshChebiMapping;    }    public boolean dropAll() {        String tableName = MeshChebiMapping.class.getAnnotation(Table.class).name();        int numberOfDeletedRecords = entityManager.createSQLQuery("delete from " + tableName).executeUpdate();        return numberOfDeletedRecords > 0;    }}