package org.zfin.datatransfer.ctd;import org.hibernate.Session;import org.springframework.stereotype.Service;import org.zfin.mutant.presentation.ChebiFishModelDisplay;import javax.persistence.Table;import java.util.List;@Servicepublic class MeshChebiDAO {    protected Session entityManager;    public MeshChebiDAO(Session entityManager) {        this.entityManager = entityManager;    }    public MeshChebiMapping find(long id) {        return entityManager.load(MeshChebiMapping.class, id);    }    List<MeshChebiMapping> allMeshChebiMapping;    public List<MeshChebiMapping> findAll() {        if (allMeshChebiMapping != null)            return allMeshChebiMapping;        allMeshChebiMapping = entityManager.createQuery("from MeshChebiMapping ", MeshChebiMapping.class).getResultList();        return allMeshChebiMapping;    }    public boolean dropAll() {        String tableName = MeshChebiMapping.class.getAnnotation(Table.class).name();        entityManager.createSQLQuery("delete from "+tableName);        entityManager.        return allMeshChebiMapping;    }}