package org.zfin.datatransfer.ctd;import lombok.Data;import javax.persistence.*;@Entity@Table(name = "mesh_chebi_mapping")@Datapublic class MeshChebiMapping {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "mcm_id")    private long id;    @Column(name = "mcm_mesh_id")    private String meshID;    @Column(name = "mcm_chebi_id")    private String chebiID;}