package org.zfin.framework.dao;import lombok.extern.log4j.Log4j2;import org.springframework.stereotype.Repository;import org.zfin.framework.HibernateUtil;import org.zfin.framework.entity.BaseEntity;import javax.persistence.EntityManager;@Log4j2@Repositorypublic class BaseSQLDAO<E extends BaseEntity> extends BaseEntityDAO<E> {    public EntityManager entityManager;    protected BaseSQLDAO(Class<E> myClass) {        super(myClass);    }    public E persist(E entity) {        log.debug("SqlDAO: persist: " + entity);        entityManager.persist(entity);        return entity;    }}